import { jsx } from '@emotion/core';
import { FieldContainer, FieldLabel, FieldDescription, FieldInput } from '@arch-ui/fields';
import { TextDayTimePicker } from '@arch-ui/day-picker';

/** @jsx jsx */

const DateTimeField = ({
  autoFocus,
  field,
  onChange,
  value,
  errors,
  isDisabled
}) => {
  const htmlID = `ks-input-${field.path}`;
  return jsx(FieldContainer, null, jsx(FieldLabel, {
    htmlFor: htmlID,
    field: field,
    errors: errors
  }), jsx(FieldDescription, {
    text: field.adminDoc
  }), jsx(FieldInput, null, jsx(TextDayTimePicker, {
    id: htmlID,
    date: value,
    onChange: onChange,
    autoFocus: autoFocus,
    disabled: isDisabled
  })));
};

export default DateTimeField;
