import _defineProperty from '@babel/runtime/helpers/esm/defineProperty';
import FieldController from '../../../../../Controller/dist/fields.esm.js';
import '@babel/runtime/helpers/objectWithoutProperties';
import '@babel/runtime/helpers/classCallCheck';
import 'lodash.isequal';

class FloatController extends FieldController {
  constructor(...args) {
    super(...args);

    _defineProperty(this, "getFilterGraphQL", ({
      type,
      value
    }) => {
      const key = type === 'is' ? `${this.path}` : `${this.path}_${type}`;
      return {
        [key]: parseFloat(value)
      };
    });

    _defineProperty(this, "getFilterLabel", ({
      label
    }) => {
      return `${this.label} ${label.toLowerCase()}`;
    });

    _defineProperty(this, "formatFilter", ({
      label,
      value
    }) => {
      return `${this.getFilterLabel({
        label
      })}: "${value}"`;
    });

    _defineProperty(this, "serialize", data => {
      const value = data[this.path];

      if (typeof value === 'number') {
        return value;
      } else if (typeof value === 'string' && value.length > 0) {
        // The field component enforces numeric values
        return parseFloat(value);
      } else {
        // if it is not a String or a Number then the field must be empty
        return null;
      }
    });

    _defineProperty(this, "getFilterTypes", () => [{
      type: 'is',
      label: 'Is exactly',
      getInitialValue: () => ''
    }, {
      type: 'not',
      label: 'Is not exactly',
      getInitialValue: () => ''
    }, {
      type: 'gt',
      label: 'Is greater than',
      getInitialValue: () => ''
    }, {
      type: 'lt',
      label: 'Is less than',
      getInitialValue: () => ''
    }, {
      type: 'gte',
      label: 'Is greater than or equal to',
      getInitialValue: () => ''
    }, {
      type: 'lte',
      label: 'Is less than or equal to',
      getInitialValue: () => ''
    }]);
  }

}

export default FloatController;
